import React, { useEffect } from 'react';
import Section from '../UI/Section';
import TaskForm from './TaskForm';
import useHttp from '../hooks/use-http';

const NewTask = (props) => {
  const { isLoading, error, sendRequest: sendTaskRequest } = useHttp();




  useEffect(() => {

    const Transform = (d) => {
      const generatedId = d.name; // firebase-specific => "name" contains generated id
      const createdTask = { id: generatedId, text: data };

      props.onAddTask(createdTask);
    }


    const enterTaskHandler = (data) => {
      sendTaskRequest({
        url: 'https://react-http-38b54-default-rtdb.firebaseio.com/tasks.json',
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: { text: data },
      },
        Transform);
    }




  }, [sendTaskRequest])










  return (
    <Section>
      <TaskForm onEnterTask={enterTaskHandler} loading={isLoading} />
      {error && <p>{error}</p>}
    </Section>
  );
};


export default NewTask;
