import React, { useEffect, useState } from 'react';
import Tasks from './components/Tasks/Tasks';
import NewTask from './components/NewTask/NewTask';
import useHttp from './components/hooks/use-http';

function App() {

  const [tasks, setTasks] = useState([]);
  // console.log("1");
  const { isLoading, error, sendRequest: fetchTasks } = useHttp();

  const transformTasks = (tasksObj) => {
    // console.log("4");
    const loadedTasks = [];
    for (const taskKey in tasksObj) {
      loadedTasks.push({ id: taskKey, text: tasksObj[taskKey].text });
    }
    setTasks(loadedTasks);
  };

  useEffect(() => {


    // console.log("2");
    fetchTasks({ url: 'https://react-http-38b54-default-rtdb.firebaseio.com/tasks.json' },
      transformTasks
    );
  }, [fetchTasks]);




  const taskAddHandler = (task) => {
    setTasks((prevTasks) => prevTasks.concat(task));
  };



  return (
    <>
      <NewTask onAddTask={taskAddHandler} />
      <Tasks items={tasks} loading={isLoading} error={error}
        onFetch={() => fetchTasks({ url: 'https://react-http-38b54-default-rtdb.firebaseio.com/tasks.json' }, transformTasks)} />
    </>
  );
}

export default App;
