function App() {


  const tasks = useFetch("GET");


  useEffect(() => {
    fetchTasks();
  }, []);



  const taskAddHandler = (task) => {
    setTasks((prevTasks) => prevTasks.concat(task));
  };



  return (
    <>
      <NewTask onAddTask={taskAddHandler} />
      <Tasks items={tasks} loading={isLoading} error={error} onFetch={fetchTasks} />
    </>
  );
}

